package problem18

type Edge struct {
	ID        int
	NodeValue NodeValue
	NodeLeft  Node
	NodeRight Node
	Parent    Node
}

func (n *Edge) Value() NodeValue {
	return n.NodeValue
}

func (n *Edge) Left() Node {
	return n.NodeLeft
}

func (n *Edge) Right() Node {
	return n.NodeRight
}

func (n *Edge) Kind() string {
	return "edge"
}

func (n *Edge) CreateChild(value NodeValue, id int) Node {
	// When the left child is nil, it's a left edge
	if n.NodeLeft == nil {
		return &Edge{
			ID:        id,
			NodeValue: value,
			Parent:    n,
			NodeLeft:  nil,
			NodeRight: nil,
		}
	}

	// When the left child is a leaf, it's a right edge
	if n.NodeLeft.Kind() == "leaf" {
		return &Edge{
			ID:        id,
			NodeValue: value,
			Parent:    n,
			NodeLeft:  nil,
			NodeRight: nil,
		}
	}

	return &Leaf{
