	return n.NodeLeft == nil || n.NodeRight == nil
}

func (n *Leaf) LeftIsNil() bool {
	return n.NodeLeft == nil
}

func (n *Leaf) RightIsNil() bool {
	return n.NodeRight == nil
}

func (n *Leaf) SetParent(node Node) {
	n.Parent = node
}

