		weightNextNode(i)
	}

	var max, maxWeight uint64
	for k, v := range dictionary {
		if v > maxWeight {
			max = k
			maxWeight = v
		}
	}

	return max
}

func weightNextNode(current uint64) uint64 {
	var next, weight uint64
	if current%2 == 0 {
		next = current / 2
	} else {
		next = (3 * current) + 1
	}
	if v, ok := dictionary[next]; !ok {
		weight = weightNextNode(next) + 1
	} else {
		weight = v + 1
	}
	dictionary[current] = weight
	return weight
}

