		// Extend the sixteen 32-bit words into eighty 32-bit words
		for j := 16; j < 80; j++ {
			w[j] = leftRotate(w[j-3]^w[j-8]^w[j-14]^w[j-16], 1)
		}

		// Initialize hash value for this chunk
		A, B, C, D, E := a, b, c, d, e

		// Main loop
		for j := 0; j < 80; j++ {
			var f, k uint32
			switch {
			case j < 20:
				f = (B & C) | ((^B) & D)
				k = 0x5A827999
			case j < 40:
				f = B ^ C ^ D
				k = 0x6ED9EBA1
			case j < 60:
				f = (B & C) | (B & D) | (C & D)
				k = 0x8F1BBCDC
			default:
