	binary.BigEndian.PutUint32(digest[4:8], b)
	binary.BigEndian.PutUint32(digest[8:12], c)
	binary.BigEndian.PutUint32(digest[12:16], d)
	binary.BigEndian.PutUint32(digest[16:20], e)

