#include <stdio.h>

long long factorial(int n) {
    long long res = 1;
    for (int i = 2; i <= n; i++) {
        res *= i;
    }
    return res;
}

int main() {
    printf("Factorial of 5 is %lld\n", factorial(5));
    return 0;
}
