solution.code:
package main

import "fmt"

func binarySearch(arr []int, x int) int {
	l, r := 0, len(arr)-1
	for l <= r {
		m := l + (r-l)/2
		if arr[m] == x {
			return m
		}
		if arr[m] < x {
			l = m + 1
		} else {
			r = m - 1
		}
	}
	return -1
}

func main() {
	arr := []int{1, 2, 3, 4, 10}
	fmt.Println(binarySearch(arr, 10))
}
