solution.code:
package main

import "fmt"

func factorial(n int) int {
	res := 1
	for i := 1; i <= n; i++ {
		res *= i
	}
	return res
}

func main() {
	fmt.Printf("Factorial of 5 is %d\n", factorial(5))
}
