solution.code:
const binarySearch = (arr, x) => {
    let l = 0, r = arr.length - 1;
    while (l <= r) {
        const m = l + Math.floor((r - l) / 2);
        if (arr[m] === x) return m;
        if (arr[m] < x) l = m + 1;
        else r = m - 1;
    }
    return -1;
};

const arr = [1, 2, 3, 4, 10];
binarySearch(arr, 10);
