const binarySearch = (arr, x) => {
    let l = 0, r = arr.length - 1;
    while (l <= r) {
        let m = l + Math.floor((r - l) / 2);
        if (arr[m] === x) return m;
        if (arr[m] < x) l = m + 1;
        else r = m - 1;
    }
    return -1;
};

const arr = [2, 3, 4, 10, 40];
const result = binarySearch(arr, 10);
console.log(result === -1 ? "Not found" : `Found at index ${result}`);

